import React, { useState, useEffect, useCallback } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function Tour() {
  const [tours, setTours] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedTour, setSelectedTour] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [formData, setFormData] = useState({ parentName: '', studentId: '', bookingDate: '' });
  const token = localStorage.getItem('access_token');

  const fetchTours = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/tours/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setTours(data);
    } catch (error) {
      console.error('Failed to fetch tours:', error);
    }
  }, [token]);

  const fetchStudents = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/students/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setStudents(data);
    } catch (error) {
      console.error('Failed to fetch students:', error);
    }
  }, [token]);

  useEffect(() => {
    fetchTours();
    fetchStudents();
  }, [fetchTours, fetchStudents]);

  const openModal = (tour) => {
    setSelectedTour(tour);
    setFormData({ parentName: '', studentId: '', bookingDate: '' });
    setModalVisible(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { parentName, studentId, bookingDate } = formData;

    if (!parentName || !studentId || !bookingDate) {
      Swal.fire('Error', 'All fields are required.', 'error');
      return;
    }

    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          parent_name: parentName,
          student: studentId,
          tour: selectedTour.tour_id,
          booking_date: bookingDate,
        }),
      });

      if (res.ok) {
        Swal.fire('Success!', 'Booking confirmed.', 'success');
        setModalVisible(false);
      } else {
        const data = await res.json();
        Swal.fire('Error', data.message || 'Booking failed.', 'error');
      }
    } catch (error) {
      Swal.fire('Error', 'Server error.', 'error');
    }
  };

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Parent - Tour Booking</h2>

      <div className="tour-grid">
        {tours.map((tour) => (
          <div key={tour.tour_id} className="tour-card">
            <h3>{tour.title}</h3>
            <p><strong>Description:</strong> {tour.description}</p>
            <p><strong>Date:</strong> {tour.date}</p>
            <p><strong>Destination:</strong> {tour.destination}</p>
            <button onClick={() => openModal(tour)}>Book Now</button>
          </div>
        ))}
      </div>

      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <h4>Book Tour: {selectedTour.title}</h4>
            <form onSubmit={handleSubmit}>
              <label>Parent Name:</label>
              <input
                type="text"
                name="parentName"
                value={formData.parentName}
                onChange={handleChange}
                required
              />

              <label>Select Student:</label>
              <select name="studentId" value={formData.studentId} onChange={handleChange} required>
                <option value="">-- Choose Student --</option>
                {students.map((s) => (
                  <option key={s.student_id} value={s.student_id}>{s.name}</option>
                ))}
              </select>

              <label>Booking Date:</label>
              <input
                type="date"
                name="bookingDate"
                value={formData.bookingDate}
                onChange={handleChange}
                required
              />

              <div style={{ marginTop: '10px' }}>
                <button type="submit">Confirm Booking</button>
                <button type="button" onClick={() => setModalVisible(false)} style={{ marginLeft: '10px' }}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
}

export default Tour;
