import React, { useState, useEffect } from 'react';
import Footer from '../components/Footer';
import '../css/Login.css';

function StaffPayment() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Unauthorized: No token found.');
      setLoading(false);
      return;
    }

    fetch('http://localhost:8000/api/payments/', {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch payments');
        }
        return res.json();
      })
      .then((data) => {
        setPayments(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError('Failed to load payments.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="dashboard">
      <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
        <h2>Staff - View Payments</h2>

        {loading ? (
          <p>Loading payments...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : payments.length === 0 ? (
          <p>No payment records found.</p>
        ) : (
          <table className="student-table" style={{ marginTop: '20px' }}>
            <thead>
              <tr>
                <th>Parent</th>
                <th>Student</th>
                <th>Amount</th>
                <th>Method</th>
                <th>Date</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((payment, index) => (
                <tr key={index}>
                  <td>{payment.parent_name}</td>
                  <td>{payment.student_name}</td>
                  <td>{payment.amount}</td>
                  <td>{payment.methods}</td>
                  <td>{payment.payment_date}</td>
                  <td>{payment.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <Footer />
      </div>
    </div>
  );
}

export default StaffPayment;
