import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function Payment() {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);

  const [formData, setFormData] = useState({
    amount: '',
    method: '',
    status: 'pending',
    date: '',
    student: '',
  });

  const [showModal, setShowModal] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [error, setError] = useState('');

  const token = localStorage.getItem('access_token');

  useEffect(() => {
    fetchBookings();
    fetchPayments();
    fetchStudents();
  }, []);

  const fetchBookings = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error('Error fetching bookings:', err);
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/payments/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setPayments(data);
    } catch (err) {
      console.error('Error fetching payments:', err);
    }
  };

  const fetchStudents = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/students/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setStudents(data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const openModal = (index = null, booking = null) => {
    if (index !== null) {
      const selectedPayment = payments[index];
      setFormData({
        amount: selectedPayment.amount,
        method: selectedPayment.methods,
        status: selectedPayment.status,
        date: selectedPayment.payment_date,
        student: selectedPayment.student_id || '',
      });
      setEditIndex(index);
    } else if (booking) {
      setSelectedBooking(booking);
      setFormData({
        amount: '',  // Parent will enter it manually
        method: '',
        status: 'pending',
        date: '',
        student: booking.student || '',
      });
      setEditIndex(null);
    } else {
      setFormData({
        amount: '',
        method: '',
        status: 'pending',
        date: '',
        student: '',
      });
      setEditIndex(null);
    }
    setError('');
    setShowModal(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { amount, method, status, date, student } = formData;

    const trimmedAmount = amount.toString().trim();
    const trimmedMethod = method.trim();
    const trimmedStatus = status.trim();
    const trimmedDate = date.trim();
    const studentId = parseInt(student);

    if (!trimmedAmount || !trimmedMethod || !trimmedStatus || !trimmedDate || isNaN(studentId) || !studentId) {
      setError('Please ensure all fields are filled correctly.');
      return;
    }

    const payload = {
      amount: parseInt(trimmedAmount),
      methods: trimmedMethod,
      status: trimmedStatus,
      payment_date: trimmedDate,
      student: studentId,
    };

    const url = editIndex !== null
      ? `http://127.0.0.1:8000/api/payments/${payments[editIndex].payment_id}/`
      : 'http://127.0.0.1:8000/api/payments/';
    const methodType = editIndex !== null ? 'PUT' : 'POST';

    try {
      const res = await fetch(url, {
        method: methodType,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        Swal.fire('Success', `Payment ${editIndex !== null ? 'updated' : 'added'} successfully`, 'success');
        fetchPayments();
        setShowModal(false);
        setFormData({ amount: '', method: '', status: 'pending', date: '', student: '' });
        setEditIndex(null);
      } else {
        const error = await res.json();
        Swal.fire('Error', error.message || 'Something went wrong', 'error');
      }
    } catch (err) {
      Swal.fire('Error', 'Server error', 'error');
    }
  };

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Payments</h2>

      <h3>Bookings</h3>
      <table className="student-table">
        <thead>
          <tr>
            <th>Tour</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((b) => (
            <tr key={b.booking_id}>
              <td>{b.parent_name} - {b.booking_date}</td>
              <td>
                {b.status?.toLowerCase() !== 'paid' && (
                  <button onClick={() => openModal(null, b)}>Add Payment</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Payment Records</h3>
      <table className="student-table">
        <thead>
          <tr>
            <th>Payment ID</th>
            <th>Amount</th>
            <th>Method</th>
            <th>Status</th>
            <th>Date</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {payments.map((p, index) => (
            <tr key={index}>
              <td>{p.payment_id}</td>
              <td>{p.amount}</td>
              <td>{p.methods}</td>
              <td>{p.status}</td>
              <td>{p.payment_date}</td>
              <td>
                <button className="small-btn edit" onClick={() => openModal(index)}>
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h4>{editIndex !== null ? 'Edit Payment' : 'Add Payment'}</h4>
            <form onSubmit={handleSubmit}>
              <label>Amount:</label>
              <input
                type="number"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                required
              />

              <label>Method:</label>
              <select name="method" value={formData.method} onChange={handleChange} required>
                <option value="">-- Select Method --</option>
                <option value="cash">Cash</option>
                <option value="card">Card</option>
                <option value="bank">Bank</option>
                <option value="mobile">Mobile</option>
              </select>

              <label>Status:</label>
              <input type="text" name="status" value={formData.status} disabled />

              <label>Date:</label>
              <input type="date" name="date" value={formData.date} onChange={handleChange} required />

              {error && <p className="error">{error}</p>}
              <button type="submit">{editIndex !== null ? 'Update' : 'Save'} Payment</button>
              <button type="button" onClick={() => setShowModal(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
}

export default Payment;
