import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function Payment() {
  const [bookings, setBookings] = useState([]);
  const [payments, setPayments] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);

  const [formData, setFormData] = useState({
    amount: '',
    method: '',
    status: 'pending',
    date: '',
    student: '',
  });

  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState('');
  const token = localStorage.getItem('access_token');

  useEffect(() => {
    fetchBookings();
    fetchPayments();
    fetchStudents();
  }, []);

  const fetchBookings = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (Array.isArray(data)) {
        setBookings(data);
      } else {
        console.error('API did not return an array:', data);
        setBookings([]);
      }
    } catch (err) {
      console.error('Error fetching bookings:', err);
      setBookings([]);
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/payments/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setPayments(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Error fetching payments:', err);
    }
  };

  const fetchStudents = async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/students/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setStudents(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const openModal = (booking) => {
    setSelectedBooking(booking);
    setFormData({
      amount: booking.amount || '',
      method: '',
      status: 'pending',
      date: '',
      student: booking.student,  // Automatically assign student ID
    });
    setError('');
    setShowModal(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { amount, method, status, date, student } = formData;

    if (!amount || !method || !status || !date || !student) {
      setError('All fields are required.');
      return;
    }

    const payload = {
      amount: parseInt(amount),
      methods: method,
      status,
      payment_date: date,
      student: parseInt(student),
    };

    try {
      const res = await fetch('http://127.0.0.1:8000/api/payments/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        Swal.fire('Success', 'Payment submitted successfully.', 'success');
        fetchPayments();
        setShowModal(false);
      } else {
        const err = await res.json();
        Swal.fire('Error', err.message || 'Failed to submit payment.', 'error');
      }
    } catch {
      Swal.fire('Error', 'Server error.', 'error');
    }
  };

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Payments</h2>

      <table className="student-table" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#eee' }}>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>Tour</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>Amount</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>Status</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>Action</th>
          </tr>
        </thead>
        <tbody>
          {bookings.length > 0 ? (
            bookings.map((b) => (
              <tr key={b.booking_id}>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{b.tour_title}</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{b.amount} TZS</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{b.status}</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>
                  {b.status.toLowerCase() !== 'paid' && (
                    <button onClick={() => openModal(b)}>Add Payment</button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ padding: '8px' }}>No bookings found.</td>
            </tr>
          )}
        </tbody>
      </table>

      {showModal && selectedBooking && (
        <div className="modal">
          <div className="modal-content">
            <h4>Add Payment</h4>
            <form onSubmit={handleSubmit}>
              <label>Amount (TZS):</label>
              <input type="number" value={formData.amount} readOnly />

              <label>Method:</label>
              <select name="method" value={formData.method} onChange={handleChange} required>
                <option value="">-- Select Method --</option>
                <option value="cash">Cash</option>
                <option value="card">Card</option>
                <option value="bank">Bank</option>
                <option value="mobile">Mobile</option>
              </select>

              <label>Status:</label>
              <input type="text" name="status" value={formData.status} readOnly />

              <label>Payment Date:</label>
              <input type="date" name="date" value={formData.date} onChange={handleChange} required />

              {error && <p className="error">{error}</p>}
              <button type="submit">Submit Payment</button>
              <button type="button" onClick={() => setShowModal(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
}

export default Payment;
