import React, { useState, useEffect, useCallback } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function Tour() {
  const [tours, setTours] = useState([]);
  const [students, setStudents] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTour, setSelectedTour] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [formData, setFormData] = useState({ studentId: '', bookingDate: '' });

  const token = localStorage.getItem('access_token');

  // Fetch tours
  const fetchTours = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/tours/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setTours(Array.isArray(data) ? data : []); // Ensure array
    } catch (error) {
      console.error('Failed to fetch tours:', error);
      setTours([]);
    }
  }, [token]);

  // Fetch students
  const fetchStudents = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/students/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setStudents(Array.isArray(data) ? data : []); // Ensure array
    } catch (error) {
      console.error('Failed to fetch students:', error);
      setStudents([]);
    }
  }, [token]);

  // Fetch bookings
  const fetchBookings = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setBookings(Array.isArray(data) ? data : []); // Ensure bookings is array
    } catch (error) {
      console.error('Failed to fetch bookings:', error);
      setBookings([]);
    }
  }, [token]);

  useEffect(() => {
    fetchTours();
    fetchStudents();
    fetchBookings();
  }, [fetchTours, fetchStudents, fetchBookings]);

  const openModal = (tour) => {
    setSelectedTour(tour);
    setFormData({ studentId: '', bookingDate: '' });
    setModalVisible(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { studentId, bookingDate } = formData;

    if (!studentId || !bookingDate || !selectedTour) {
      Swal.fire('Error', 'All fields are required.', 'error');
      return;
    }

    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          student: studentId,
          tour: selectedTour.tour_id,
          booking_date: bookingDate,
        }),
      });

      if (res.ok) {
        Swal.fire('Success!', 'Booking confirmed.', 'success');
        setModalVisible(false);
        fetchBookings(); // Refresh bookings after new booking
      } else {
        const data = await res.json();
        Swal.fire('Error', data.message || 'Booking failed.', 'error');
      }
    } catch (error) {
      Swal.fire('Error', 'Server error.', 'error');
    }
  };

  const handlePayment = (bookingId) => {
    Swal.fire({
      title: 'Make Payment',
      text: `Proceed to payment for booking ID ${bookingId}?`,
      icon: 'info',
      showCancelButton: true,
      confirmButtonText: 'Pay Now',
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire('Paid!', 'Your booking has been marked as paid.', 'success');
        // Optionally update backend booking status here
      }
    });
  };

  const todayDate = new Date().toISOString().split('T')[0];

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Parent - Tour Booking</h2>

      {/* Tour Cards */}
      <div className="tour-grid">
        {tours.map((tour) => (
          <div key={tour.tour_id} className="tour-card">
            <h3>{tour.title}</h3>
            <p><strong>Description:</strong> {tour.description}</p>
            <p><strong>Date:</strong> {tour.date}</p>
            <p><strong>Destination:</strong> {tour.destination}</p>
            <button onClick={() => openModal(tour)}>Book Now</button>
          </div>
        ))}
      </div>

      {/* Booking Modal */}
      {modalVisible && selectedTour && (
        <div className="modal">
          <div className="modal-content">
            <h4>Book Tour: {selectedTour.title}</h4>
            <form onSubmit={handleSubmit}>
              <label>Select Student:</label>
              <select
                name="studentId"
                value={formData.studentId}
                onChange={handleChange}
                required
              >
                <option value="">-- Choose Student --</option>
                {students.map((s) => (
                  <option key={s.student_id} value={s.student_id}>
                    {s.name}
                  </option>
                ))}
              </select>

              <label>Booking Date:</label>
              <input
                type="date"
                name="bookingDate"
                min={todayDate}
                value={formData.bookingDate}
                onChange={handleChange}
                required
              />

              <div style={{ marginTop: '10px' }}>
                <button type="submit">Confirm Booking</button>
                <button
                  type="button"
                  onClick={() => setModalVisible(false)}
                  style={{ marginLeft: '10px' }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Booking List */}
      <div className="booking-section">
        <h3>Your Bookings</h3>
        {!Array.isArray(bookings) || bookings.length === 0 ? (
          <p>No bookings found.</p>
        ) : (
          bookings.map((booking) => (
            <div key={booking.booking_id} className="booking-card">
              <p><strong>Tour:</strong> {booking.tour_title}</p>
              <p><strong>Student:</strong> {booking.student_name}</p>
              <p><strong>Date:</strong> {booking.booking_date}</p>
              <p><strong>Status:</strong> {booking.status}</p>
              {booking.status === 'pending' && (
                <button onClick={() => handlePayment(booking.booking_id)}>Make Payment</button>
              )}
            </div>
          ))
        )}
      </div>

      <Footer />
    </div>
  );
}

export default Tour;
