import React, { useState, useEffect, useCallback } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function Tour() {
  const [tours, setTours] = useState([]);
  const [students, setStudents] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTour, setSelectedTour] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [formData, setFormData] = useState({ studentId: '', bookingDate: '' });
  const [editingBookingId, setEditingBookingId] = useState(null); // To track if updating a booking

  const token = localStorage.getItem('access_token');

  // Fetch tours
  const fetchTours = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/tours/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setTours(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch tours:', error);
      setTours([]);
    }
  }, [token]);

  // Fetch students
  const fetchStudents = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/students/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setStudents(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch students:', error);
      setStudents([]);
    }
  }, [token]);

  // Fetch bookings
  const fetchBookings = useCallback(async () => {
    try {
      const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setBookings(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch bookings:', error);
      setBookings([]);
    }
  }, [token]);

  useEffect(() => {
    fetchTours();
    fetchStudents();
    fetchBookings();
  }, [fetchTours, fetchStudents, fetchBookings]);

  // Open modal for new booking
  const openModal = (tour) => {
    setSelectedTour(tour);
    setFormData({ studentId: '', bookingDate: '' });
    setEditingBookingId(null);
    setModalVisible(true);
  };

  // Open modal to update existing booking
  const openUpdateModal = (booking) => {
    setSelectedTour(tours.find((t) => t.tour_id === booking.tour)); // set tour from booking
    setFormData({ 
      studentId: booking.student, 
      bookingDate: booking.booking_date 
    });
    setEditingBookingId(booking.booking_id);
    setModalVisible(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { studentId, bookingDate } = formData;

    if (!studentId || !bookingDate || !selectedTour) {
      Swal.fire('Error', 'All fields are required.', 'error');
      return;
    }

    try {
      if (editingBookingId) {
        // Update existing booking
        const res = await fetch(`http://127.0.0.1:8000/api/bookings/${editingBookingId}/`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            student: studentId,
            booking_date: bookingDate,
          }),
        });

        if (res.ok) {
          Swal.fire('Success!', 'Booking updated.', 'success');
          setModalVisible(false);
          fetchBookings();
        } else {
          const data = await res.json();
          Swal.fire('Error', data.message || 'Update failed.', 'error');
        }
      } else {
        // Create new booking
        const res = await fetch('http://127.0.0.1:8000/api/bookings/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            student: studentId,
            tour: selectedTour.tour_id,
            booking_date: bookingDate,
          }),
        });

        if (res.ok) {
          Swal.fire('Success!', 'Booking confirmed.', 'success');
          setModalVisible(false);
          fetchBookings();
        } else {
          const data = await res.json();
          Swal.fire('Error', data.message || 'Booking failed.', 'error');
        }
      }
    } catch (error) {
      Swal.fire('Error', 'Server error.', 'error');
    }
  };

  const todayDate = new Date().toISOString().split('T')[0];

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Parent - Tour Booking</h2>

      {/* Tour Cards */}
      <div className="tour-grid">
        {tours.map((tour) => (
          <div key={tour.tour_id} className="tour-card">
            <h3>{tour.title}</h3>
            <p><strong>Description:</strong> {tour.description}</p>
            <p><strong>Date:</strong> {tour.date}</p>
            <p><strong>Destination:</strong> {tour.destination}</p>
            <button onClick={() => openModal(tour)}>Book Now</button>
          </div>
        ))}
      </div>

      {/* Booking Modal */}
      {modalVisible && selectedTour && (
        <div className="modal">
          <div className="modal-content">
            <h4>{editingBookingId ? 'Update Booking' : `Book Tour: ${selectedTour.title}`}</h4>
            <form onSubmit={handleSubmit}>
              <label>Select Student:</label>
              <select
                name="studentId"
                value={formData.studentId}
                onChange={handleChange}
                required
              >
                <option value="">-- Choose Student --</option>
                {students.map((s) => (
                  <option key={s.student_id} value={s.student_id}>
                    {s.name}
                  </option>
                ))}
              </select>

              <label>Booking Date:</label>
              <input
                type="date"
                name="bookingDate"
                min={todayDate}
                value={formData.bookingDate}
                onChange={handleChange}
                required
                disabled={!!editingBookingId} // disable date change on update (optional)
              />

              <div style={{ marginTop: '10px' }}>
                <button type="submit">{editingBookingId ? 'Update Booking' : 'Confirm Booking'}</button>
                <button
                  type="button"
                  onClick={() => setModalVisible(false)}
                  style={{ marginLeft: '10px' }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Booking List */}
      <div className="booking-section">
        <h3>Your Bookings</h3>
        {!Array.isArray(bookings) || bookings.length === 0 ? (
          <p>No bookings found.</p>
        ) : (
          bookings.map((booking) => (
            <div key={booking.booking_id} className="booking-card">
              <p><strong>Tour:</strong> {booking.tour_title}</p>
              <p><strong>Student:</strong> {booking.student_name}</p>
              <p><strong>Date:</strong> {booking.booking_date}</p>
              <p><strong>Status:</strong> {booking.status}</p>
              {booking.status === 'pending' && (
                <button onClick={() => openUpdateModal(booking)}>Update</button>
              )}
            </div>
          ))
        )}
      </div>

      <Footer />
    </div>
  );
}

export default Tour;
