import React, { useState, useEffect } from 'react';
import Footer from '../components/Footer';
import '../css/Login.css';

function StaffPayment() {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('http://localhost:8000/api/payments/') // ⚠️ Replace with your real API URL
      .then((res) => {
        if (!res.ok) {
          throw new Error('Failed to fetch payments');
        }
        return res.json();
      })
      .then((data) => {
        setPayments(data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to load payments');
        setLoading(false);
        console.error(err);
      });
  }, []);

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Staff - View Payments</h2>

      {loading ? (
        <p>Loading payments...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : payments.length === 0 ? (
        <p>No payment records found.</p>
      ) : (
        <table className="student-table" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Parent</th>
              <th>Student</th>
              <th>Amount</th>
              <th>Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {payments.map((payment, index) => (
              <tr key={index}>
                <td>{payment.parent_name || payment.parent?.name || 'N/A'}</td>
                <td>{payment.student_name || payment.student?.name || 'N/A'}</td>
                <td>{payment.amount}</td>
                <td>{payment.date}</td>
                <td>{payment.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <Footer />
    </div>
  );
}

export default StaffPayment;
