import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Footer from '../components/Footer';
import '../css/Login.css';

function StaffPayment() {
  const [payments, setPayments] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch payments from backend API
    fetch('/api/payments')
      .then(response => response.json())
      .then(data => setPayments(data))
      .catch(() => setError('Failed to fetch payments'));
  }, []);

  const handleStatusChange = (index, newStatus) => {
    const updatedPayments = [...payments];
    const paymentToUpdate = updatedPayments[index];
    paymentToUpdate.status = newStatus;

    // Update backend
    fetch(`/api/payments/${paymentToUpdate.payment_id}/`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: newStatus }),
    })
      .then(response => {
        if (!response.ok) throw new Error('Failed to update status');
        return response.json();
      })
      .then(() => {
        setPayments(updatedPayments);
        Swal.fire('Success', 'Payment status updated', 'success');
      })
      .catch(() => setError('Failed to update payment status'));
  };

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Staff - View Payments</h2>
      {error && <p className="error">{error}</p>}
      {payments.length === 0 ? (
        <p>No payment records found.</p>
      ) : (
        <table className="student-table" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Parent</th>
              <th>Student</th>
              <th>Amount</th>
              <th>Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {payments.map((payment, index) => (
              <tr key={payment.payment_id}>
                <td>{payment.parentName}</td>
                <td>{payment.studentName}</td>
                <td>{payment.amount}</td>
                <td>{payment.date}</td>
                <td>{payment.status}</td>
                <td>
                  <select
                    value={payment.status}
                    onChange={e => handleStatusChange(index, e.target.value)}
                  >
                    <option value="pending">Pending</option>
                    <option value="paid">Paid</option>
                    <option value="failed">Failed</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <Footer />
    </div>
  );
}

export default StaffPayment;
