import React, { useEffect, useState } from 'react';

function AccuracyPage() {
  const [accuracy, setAccuracy] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('/api/accuracy/')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch accuracy data');
        return res.json();
      })
      .then(data => {
        setAccuracy(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Could not load accuracy data.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="dashboard-content" style={{ minHeight: '100vh', paddingBottom: '60px' }}>
      <h2>Accuracy Statistics</h2>
      {loading && <p>Loading accuracy data...</p>}
      {error && <p className="error">{error}</p>}
      {accuracy && (
        <>
          <div style={{
            background: '#e0ffe0',
            padding: '20px',
            borderRadius: '8px',
            marginBottom: '30px',
            display: 'inline-block'
          }}>
            <h3>Overall Accuracy: <span style={{ color: '#2e7d32' }}>{accuracy.overall_accuracy}%</span></h3>
          </div>
          <table className="student-table" style={{ marginTop: '20px', width: '100%' }}>
            <thead>
              <tr>
                <th>Name</th>
                <th>Accuracy (%)</th>
              </tr>
            </thead>
            <tbody>
              {accuracy.details.map((item, idx) => (
                <tr key={idx}>
                  <td>{item.name}</td>
                  <td>{item.accuracy}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default AccuracyPage;
